import React, { useState, useEffect } from 'react';
import './App.css';
import WalletConnection from './components/WalletConnection';
import MarketSelector from './components/MarketSelector';
import OrderBook from './components/OrderBook';
import TradeInterface from './components/TradeInterface';
import { ClobClient, OrderType } from '@polymarket/clob-client';

function App() {
  const [account, setAccount] = useState(null);
  const [provider, setProvider] = useState(null);
  const [clobClient, setClobClient] = useState(null);
  const [markets, setMarkets] = useState([]);
  const [selectedMarket, setSelectedMarket] = useState(null);
  const [orderBook, setOrderBook] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Initialize CLOB client when wallet is connected
  useEffect(() => {
    if (account && provider) {
      initializeClobClient();
    }
  }, [account, provider]); // eslint-disable-line react-hooks/exhaustive-deps

  const initializeClobClient = async () => {
    try {
      setLoading(true);
      setError(null);

      const host = 'https://clob.polymarket.com';
      const signer = provider.getSigner();

      // Initialize CLOB client and create/derive API key
      const client = new ClobClient(host, 137, signer);
      await client.createOrDeriveApiKey();

      // Set the client with credentials
      setClobClient(client);

      console.log('CLOB client initialized successfully');
    } catch (err) {
      console.error('Failed to initialize CLOB client:', err);
      setError('Failed to initialize trading client. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const fetchMarkets = async () => {
    try {
      setLoading(true);
      setError(null);

      // Use CRA proxy (setupProxy.js)
      const response = await fetch('/api/polymarket/markets?ascending=false&volume_num_min=1000&closed=false&limit=10');

      if (!response.ok) {
        throw new Error('Failed to fetch markets');
      }

      const marketsData = await response.json();
      const activeMarkets = [];

      for (const market of marketsData) {
        if (market.active && market.volume > 0) {
          // Get token IDs for this market
          const tokensResponse = await fetch(`http://localhost:3001/api/polymarket/markets/${market.id}`);
          if (tokensResponse.ok) {
            const marketData = await tokensResponse.json();
            let tokens = marketData.clobTokenIds || '';
            tokens = JSON.parse(tokens);

            if (tokens.length >= 2) {
              activeMarkets.push({
                id: market.id,
                question: market.question,
                yesTokenId: tokens[0],
                noTokenId: tokens[1],
                endDate: market.endDate,
                volume: market.volume,
                description: market.description || market.question
              });
            }
          }
        }
      }

      setMarkets(activeMarkets);
      console.log('Fetched markets:', activeMarkets);
    } catch (err) {
      console.error('Failed to fetch markets:', err);
      setError('Failed to fetch markets. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const checkMarketOrderBook = async (tokenId) => {
    try {
      if (!clobClient) {
        throw new Error('CLOB client not initialized');
      }

      const orderBookData = await clobClient.getOrderBook(tokenId);
      return orderBookData;
    } catch (err) {
      console.error('Failed to fetch order book:', err);
      setError('Failed to fetch order book data');
      return null;
    }
  };

  const placeOrder = async (tokenId, price, size, side) => {
    try {
      if (!clobClient) {
        throw new Error('CLOB client not initialized');
      }

      setLoading(true);
      setError(null);

      const orderParams = {
        tokenID: tokenId,
        price: price,
        side: side,
        size: size,
      };

      const orderConfig = {
        tickSize: "0.001",
        negRisk: false
      };

      const response = await clobClient.createAndPostOrder(
        orderParams,
        orderConfig,
        OrderType.GTC
      );

      console.log('Order placed successfully:', response);
      return response;
    } catch (err) {
      console.error('Failed to place order:', err);
      setError(`Failed to place order: ${err.message}`);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>🚀 Polymarket Trading</h1>
        <p>Trade on prediction markets with ease</p>
      </header>

      <main className="App-main">
        {!account ? (
          <WalletConnection 
            onConnect={(acc, prov) => {
              setAccount(acc);
              setProvider(prov);
            }}
          />
        ) : (
          <div className="trading-interface">
            <div className="wallet-info">
              <p>Connected: {account}</p>
              <button 
                onClick={() => {
                  setAccount(null);
                  setProvider(null);
                  setClobClient(null);
                  setMarkets([]);
                  setSelectedMarket(null);
                  setOrderBook(null);
                }}
                className="disconnect-btn"
              >
                Disconnect
              </button>
            </div>

            {error && (
              <div className="error-message">
                {error}
                <button onClick={() => setError(null)}>×</button>
              </div>
            )}

            {loading && (
              <div className="loading">
                <div className="spinner"></div>
                <p>Loading...</p>
              </div>
            )}

            <MarketSelector 
              markets={markets}
              selectedMarket={selectedMarket}
              onMarketSelect={setSelectedMarket}
              onFetchMarkets={fetchMarkets}
            />

            {selectedMarket && (
              <div className="market-details">
                <h3>{selectedMarket.question}</h3>
                <p>End Date: {new Date(selectedMarket.endDate).toLocaleDateString()}</p>
                <p>Volume: ${selectedMarket.volume.toLocaleString()}</p>
              </div>
            )}

            {selectedMarket && (
              <OrderBook 
                tokenId={selectedMarket.yesTokenId}
                onOrderBookUpdate={setOrderBook}
                onCheckOrderBook={checkMarketOrderBook}
              />
            )}

            {selectedMarket && orderBook && (
              <TradeInterface 
                market={selectedMarket}
                orderBook={orderBook}
                onPlaceOrder={placeOrder}
                loading={loading}
              />
            )}
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
